mapper.select:
select itb.instructionToBankCode, cast(itb.instruction as varchar(6500)) as instruction from InstructionToBank itb where itb.tradeServiceId = #{tradeServiceId}
union
select ritb.instructionToBankCode, cast(ritb.instruction as varchar(6500)) as instruction from RefInstructionToBank ritb where ritb.instructionToBankCode not in (select instructionToBankCode from InstructionToBank where tradeServiceId = #{tradeServiceId})
